import React from "react"
import axios from 'axios'
import { BrowserRouter, Switch, Route} from 'react-router-dom'
import Homepage from './Containers/Homepage'
import Taskbar from './Taskbar'
import Artist from './Containers/Artist'
import LoginContainer from './auth/Containers/LoginContainer'
import 'bootstrap/dist/css/bootstrap.min.css'
import { connect } from 'react-redux'
import { setUserId } from './actions'
import { setUsername } from './actions'
import { fetchLogin } from './actions'

class App extends React.Component {

state = {
  isLoggedIn: !!localStorage.getItem("username"),
}



handleLogin = (user) => {
  this.setState({
    isLoggedIn: true,
  })
  localStorage.setItem("username", user.username)
  localStorage.setItem("userId", user.id)
}

handleLogout = () => {
  this.setState({
  isLoggedIn: false,
  })
  this.props.setUserId('')
  this.props.setUsername('')
  localStorage.removeItem("username")
  localStorage.removeItem("userId")
}

componentDidMount = () => {
  if (!this.state.isLoggedIn) {
  this.loginStatus()
  }
}

loginStatus = () => {
 this.props.fetchLogin
  //   axios.get('http://localhost:3000/logged_in', 
  // {withCredentials: true})
  //   .then(response => {
  //     if (response.data.logged_in) {
  //       this.handleLogin(response.data.user)
  //     } else {
  //       this.handleLogout()
  //     }
  //   })
  //   .catch(error => console.log('api errors:', error))
}

render () {

    return (


    <BrowserRouter>
    <>
    <Taskbar loggedInStatus={this.state.isLoggedIn} handleLogout={this.handleLogout}/>
    <Switch>

      <Route exact path="/login" render={(props) => <LoginContainer {...props} handleLogin={this.handleLogin} loggedInStatus={this.state.isLoggedIn} />} />
      <Route exact path="/homepage" render={(props) => <Homepage {...props} loggedInStatus={this.state.isLoggedIn} />} />
      <Route path="/artist/:id" render={(props) => <Artist {...props} key={props.match.params.id} loggedInStatus={this.state.isLoggedIn} />}/>
    </Switch>
    </>
    </BrowserRouter>

    );
  }
}



export default connect(null, {setUserId: setUserId, setUsername: setUsername, fetchLogin: fetchLogin})(App)
