import { SET_USER_ID } from './actions'
import { SET_USERNAME } from './actions'
import { FETCH_LOGIN } from './actions'
import { IS_LOGGED_IN } from './actions'

const initialState = {userId: localStorage.getItem("userId"), username: localStorage.getItem("username"), requesting: false}

const gigApp = (state = initialState, action) => {
    switch (action.type) {
        case SET_USER_ID:
          return {...state, ...{userId: action.userId}}
        case SET_USERNAME:
            return {...state, ...{username: action.username}}
        case FETCH_LOGIN:
            return {...state, loginStatus: [...state.loginStatus], requesting: true}
        case IS_LOGGED_IN:
            return {...state, ...{loginStatus: action.loginStatus}, requesting: false}
        default:
          return state
      }
}

export default gigApp
