import React, { Component } from "react";
import Form from 'react-bootstrap/Form'
import Button from 'react-bootstrap/Button'


export default class Registration extends Component {

  state = {
      email: "",
      password: "",
      passwordConfirmation: ""
    };



  handleChange = event => {
    this.setState({
      [event.target.name]: event.target.value
    });
  }

  componentDidMount() {
      fetch("http://localhost:3000/v1/login")
      .then(response => response.json())
      .then(resp => console.log(resp.login))
  }

  handleSubmit = event => {
    event.preventDefault();
    let body = JSON.stringify({
      email: this.state.email, 
      password: this.state.password,
  })
  console.log(body)
    const loginDetails = {
        method: 'POST',
        headers: {
            'Accept': 'application/json',
            'Content-Type': 'application/json'
        },
        body: body
    }
    fetch("http://localhost:3000/v1/login", loginDetails)
      .then(response => response.json())
      .then(resp => console.log(resp))


  }

  render() {
    return (
      <div>
        {/* <form onSubmit={this.handleSubmit}>
          <input
            type="email"
            name="email"
            placeholder="Email"
            value={this.state.email}
            onChange={this.handleChange}
            required
          />

          <input
            type="password"
            name="password"
            placeholder="Password"
            value={this.state.password}
            onChange={this.handleChange}
            required
          />


          <button type="submit">Register</button>

        </form> */}

        <Form onSubmit={this.handleSubmit}>
              <Form.Group controlId="formBasicEmail">
                <Form.Label>Email address</Form.Label>
                <Form.Control type="email" placeholder="Enter email" name="email" value={this.state.email} onChange={this.handleChange} />
                <Form.Text className="text-muted">
                  We'll never share your email with anyone else.
                </Form.Text>
              </Form.Group>

              <Form.Group controlId="formBasicPassword">
                <Form.Label>Password</Form.Label>
                <Form.Control type="password" placeholder="Password" name="password" value={this.state.password} onChange={this.handleChange} />
              </Form.Group>

              <Form.Group controlId="formBasicPassword">
                <Form.Label>Confirm password</Form.Label>
                <Form.Control type="password" placeholder="Password" name="passwordConfirmation" value={this.state.passwordConfirmation} onChange={this.handleChange} />
              </Form.Group>

              <Button variant="primary" type="submit" >
                Submit
              </Button>
        </Form>
      </div>
    );
  }
}
