import React, { Component } from 'react'
import axios from 'axios'



class AllUserGigs extends Component {

    state = {
        gigNames: []
    }



    fetchGigs = () => {
        this.props.allLikedUserGigsId.map(gigId => {
            fetch(`https://api.songkick.com/api/3.0/events/${gigId}.json?apikey=XyKG4KDNOliuaDev`)
            .then(resp => resp.json())
            .then(data => this.setState(prevState => {console.log(prevState) 
                return {

                gigNames: prevState.gigNames.push(data.resultsPage.results.event.displayName)
            }})
        )})

    }

    renderGigs = () => {
        return <li>{data}</li>
    }


render() {
console.log(this.state.gigNames)
    return(
        <div>
        <ul>
        {this.fetchGigs()}
        </ul>
        </div>
    )
}






}

export default AllUserGigs
