//        </Spinner>
//     }

// }

render() {
    console.log(this.state)
    if (this.state.isLoading == false) {
        return <h1>{this.state.artistName}</h1>
    } else {
        return  <Spinner animation="border" role="status">
                   <span className="sr-only">Loading...</span>
                </Spinner>
    }
}
}

export default withRouter(Artist)
