import React, { Component } from 'react';
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import image from './default-artist.png'

class ArtistGig extends Component {

cardImage = () => {
    let venueSrc = `https://images.sk-static.com/images/media/profile_images/venues/${this.props.gig.venue.id}/col2`
    if (this.props.gig.venue.id != null) {
        return <Card.Img width="286" height="180" variant="top" src={venueSrc} />
    } else {
        return <div className="myImage"> </div>
    }

}



    render() {

        return(

        <Card>

            <Card.Body>
                {this.cardImage()}
                <Card.Title>{this.props.gig.displayName}</Card.Title>
                <Card.Text>
                {this.props.gig.venue.metroArea.displayName}, {this.props.gig.venue.metroArea.country.displayName}
                </Card.Text>
                <Button variant="primary">Go somewhere</Button>
            </Card.Body>
        </Card>

        )
    }

}

export default ArtistGig
