import React, { Component } from 'react'
import Login from './Login'
import Registration from './Registration'
import Button from 'react-bootstrap/Button'

class LoginContainer extends Component {

    state = {
        loginToggle: true,
    }

    handleToggle = () => {
        if (this.state.loginToggle) {
            return <div>
                        <Login {...props} handleLogin={this.handleLogin} loggedInStatus={this.state.isLoggedIn} />
                        <Button variant="link">Don't have an account? Click here to register</Button>
                    </div> 
        }
        if (!this.state.loginToggle) {
            return <div>
                        <Registration {...props} handleLogin={this.handleLogin} loggedInStatus={this.state.isLoggedIn} />
                        <Button variant="link">Already have an account? Click here to log in</Button>
                    </div>
        }
    }

    render() {
        return(
            <div>
                {this.handleToggle}
            </div>
        )
    }

}

export default LoginContainer
