import React, { Component } from 'react'
import Jumbotron from 'react-bootstrap/Jumbotron'
import Carousel from 'react-bootstrap/Carousel'

class GigDetails extends Component {

    renderCarousel = () => {
        return this.props.gig.performance.map(performer => {
            let imgSrc = `//images.sk-static.com/images/media/profile_images/artists/${performer.id}/huge_avatar`
            return  <Carousel.Item>
            <img
            className="d-block w-100"
            src={imgSrc}
            alt="First slide"
            />
            <Carousel.Caption>
            <h3>First slide label</h3>
            <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
            </Carousel.Caption>
            </Carousel.Item>
        })
    }

    render() {
        console.log(this.props.gig.performance)
        return(
            <Jumbotron className="bg-secondary"style={{
                color: "white"
            }}>
           <h1>{this.props.gig.displayName}</h1>
           <p>City: {this.props.gig.venue.city.displayName}, {this.props.gig.venue.city.country.displayName}</p>
           <p>Venue: {this.props.gig.venue.displayName}</p>
           <Carousel>
                {this.renderCarousel()}

            </Carousel>

           </Jumbotron>
        )
    }

}

export default GigDetails
