import React, { Component } from 'react';
import ArtistGig from '../ArtistGig'
import Row from 'react-bootstrap/Row'
import Col from 'react-bootstrap/Col'
import axios from 'axios'


class ArtistGigList extends Component {

    state = {
        userLikedGigsId: [],
        userLikedGigs: []
    }

componentDidMount = () => {
  this.getData()
}

getData = () => {
    axios.get('http://localhost:3000/gigs', {withCredentials: true})
    .then(resp => this.setState({
        userLikedGigsId: resp.data.gigs.map(gig => gig.gigId),
        userLikedGigs: resp.data.gigs
    })
    )
}

likeGig = (gigId) => {
    let likedGig = {
        userId: this.props.userId,
        gigId: gigId
    }

    axios.post('http://localhost:3000/gigs', likedGig, {withCredentials: true})
    .then(resp => this.setState({buttonDisabled: true}))
}

unlikeGig = () => {
    let removedGig = {data: {
        userId: this.props.userId,
        gigId: this.props.gig.id
    }}
axios.delete(`http://localhost:3000/gigs/2`, removedGig, {withCredentials: true})
.then(resp => this.setState({buttonDisabled: false}))
}


render() {
    console.log(this.state.userLikedGigs)
   return(

        <Row>
        {this.props.artistGigs.map((gig, index) => (
            <Col md="4" key={index}>
            <ArtistGig gig={gig} userId={this.props.userId} isLiked={this.state.userLikedGigsId.includes(gig.id)} likeGig={() => this.likeGig(gig.id)}/>
            </Col>
        ))}
        </Row>


   )



}
}

export default ArtistGigList
