import React, { Component } from 'react';
import Card from 'react-bootstrap/Card'
import Button from 'react-bootstrap/Button'
import axios from 'axios'

class ArtistGig extends Component {

    state = {
        buttonDisabled: false
    }

handleSubmit = e => {
    e.preventDefault()
    if (this.state.buttonDisabled == false) {
    let likedGig = {
        userId: this.props.userId,
        gigId: this.props.gig.id
    }

    axios.post('http://localhost:3000/gigs', likedGig, {withCredentials: true})
    .then(resp => this.setState({buttonDisabled: true}))
    } else {
        let removedGig = {params: {
            userId: this.props.userId,
            gigId: this.props.gig.id
        }}
    axios.delete(`http://localhost:3000/gigs`, removedGig, {withCredentials: true})
    .then(resp => this.setState({buttonDisabled: false}))
    }
}

componentDidMount = () => {
    if (this.props.likedGig) {
        this.setState({
            buttonDisabled: true
        })
    }
}

render() {
    console.log(this.props.likedGigObject[0].id)
        return(
        <Card >
            <Card.Body>
                <Card.Title>{this.props.gig.displayName}</Card.Title>
                <Card.Text>
                {this.props.gig.venue.metroArea.displayName}, {this.props.gig.venue.metroArea.country.displayName}
                </Card.Text>
                <Card.Text>
                    {this.props.gig.start.date}
                </Card.Text>
                <Button variant="primary" type="submit" onClick={this.handleSubmit}>Save Gig</Button>
            </Card.Body>
        </Card>

        )
    }

}

export default ArtistGig
